    /**
     *
     * @since 26/11/2015
     * @updatedBy TechnicalTeam [A.Agamy]
     * @Note added new constractors to pass exception
     * constructors generated by ExceptionHandlerProcessor
     */
    @SuppressWarnings("compatibility:-4485906751177687898")
    private static final long serialVersionUID = 1L;
    public static final String DEF_MSG = "ApplicantIsAlreadyEmployedException";
    public static final String MSG_KEY = "ApplicantIsAlreadyEmployedException";
    public static final String BDL_KEY = "ApplicantIsAlreadyEmployedException";

    public ApplicantIsAlreadyEmployedException(String message, Throwable e, String bundleKey, String messageKey) {
        super(message, e, bundleKey, messageKey);
    }


    public ApplicantIsAlreadyEmployedException(String message, Throwable e, String messageKey) {
        super(message, e, BDL_KEY, messageKey);
    }


    public ApplicantIsAlreadyEmployedException(String message, Throwable e) {
        super(message, e, BDL_KEY, MSG_KEY);
    }


    public ApplicantIsAlreadyEmployedException(Throwable e, String bundleKey, String messageKey) {
        super(DEF_MSG, e, bundleKey, messageKey);
    }


    public ApplicantIsAlreadyEmployedException(Throwable e, String messageKey) {
        super(DEF_MSG, e, BDL_KEY, messageKey);
    }


    public ApplicantIsAlreadyEmployedException(Throwable e) {
        super(DEF_MSG, e, BDL_KEY, MSG_KEY);
    }


    public ApplicantIsAlreadyEmployedException(String message, String bundleKey, String messageKey) {
        super(message, bundleKey, messageKey);
    }


    public ApplicantIsAlreadyEmployedException(String message, String messageKey) {
        super(message, BDL_KEY, messageKey);
    }


    public ApplicantIsAlreadyEmployedException() {

        super(DEF_MSG, BDL_KEY, MSG_KEY);

    }

    public ApplicantIsAlreadyEmployedException(String message) {

        super(message, BDL_KEY, MSG_KEY);

    }
